import React, { useState, useEffect } from "react";
import axios from "axios";
import io from 'socket.io-client';
import "./Dashboard.css";

// Image imports
import BallotGeneration from "./icons/ballot-generator.png";
import BallotPickup from "./icons/ballots.png";
import VoteCasting from "./icons/ballotbox.png";
import ReceiptScanning from "./icons/receipt.png";
import VoteProcessing from "./icons/bb.png";
import BallotAudit from "./icons/auditors-phone.png";
import ReceiptVerf from "./icons/voter-phone.png";
import DercyptVerf from "./icons/univauditors-phone.png";
import VVPATVerf from "./icons/vvpatauditors.png";
import PO from "./icons/po.png";
import Voter from "./icons/voter.png";
import VVPAT from "./icons/vvpats.png";

const App = () => {
  const [currentStep, setCurrentStep] = useState("");
  const [response, setResponse] = useState("");
  const [completedSteps, setCompletedSteps] = useState({
    Setup: false,
    Generate_Ballots: false,
    Upload_Votes: false,
    Mix_Decrypt: false,
    Audit: false,
    Voter_Verification:false,
    Decryption_Verification:false,
    VVPAT_Verification:false
  });
  const [showSuccessDialog, setShowSuccessDialog] = useState(false);
  const [socket, setSocket] = useState(null);

  // WebSocket initialization
  useEffect(() => {
    const newSocket = io('http://localhost:5000', {
      reconnection: true,
      transports: ['websocket'],
      withCredentials: true
    });
    setSocket(newSocket);

    return () => newSocket.disconnect();
  }, []);

  // Socket event listeners
  useEffect(() => {
    if (!socket) return;

    const handleStepComplete = (step) => {
      setCompletedSteps(prev => ({ ...prev, [step]: true }));
      setShowSuccessDialog(true);
      setTimeout(() => setShowSuccessDialog(false), 3000);
    };

    socket.on('step-completed', handleStepComplete);
    return () => socket.off('step-completed', handleStepComplete);
  }, [socket]);

  const handleStep = async (step) => {
    setCurrentStep(step);
    try {
      let res;
      switch (step) {
        case "setup":
          res = await axios.post("http://localhost:5000/setup", { 
            alpha: 1, 
            electionId: 123 
          });
          break;
        case "generateBallots":
          res = await axios.post("http://localhost:5000/generate", { 
            n: 10, 
            electionId: 123 
          });
          break;
        case "uploadVotes":
          res = await axios.post("http://localhost:5000/upload", { 
            votes: [] 
          });
          break;
        case "mixAndDecrypt":
          res = await axios.post("http://localhost:5000/mix");
          break;
        case "audit":
          res = await axios.post("http://localhost:5000/audit", {
            commitment: [],
            booth_num: 1,
            bid: 101,
            election_id: 123,
          });
          break;
        default:
          res = { data: "Invalid step" };
      }
      setResponse(JSON.stringify(res.data, null, 2));

      // Simulate step completion for demo purposes
      if (!completedSteps[step]) {
        setTimeout(() => {
          setCompletedSteps(prev => ({ ...prev, [step]: true }));
          setShowSuccessDialog(true);
          setTimeout(() => setShowSuccessDialog(false), 3000);
        }, 1000);
      }
    } catch (error) {
      console.error(error);
      setResponse(`Error: ${error.response?.data || error.message}`);
    }
  };

  return (
    <div className="dashboard">
      <div className="header">
        <h1>Secure Election System</h1>
      </div>

      {showSuccessDialog && (
        <div className="success-dialog">
          Step completed successfully!
        </div>
      )}

      <div className="content-area">
        <div className="main-sections">
          {/* Untrusted Section */}
          <div className="section untrusted-section">
            <div className="section-header">
              Untrusted Election Components
            </div>
            <div className="steps-grid">
              {/* Ballot Generation */}
              <div className={`step-card ${completedSteps.Setup ? 'completed' : ''}`} onClick={() => handleStep("setup")}>
                {completedSteps.setup && <div className="status-indicator">Verified</div>}
                <div className="images">
                  <img src={BallotGeneration} alt="Ballot Generation" className="image-lg"/>
                  <img src={BallotPickup} alt="Ballot Pick-up" className="image-lg" />
                </div>
                <div className="step-title">Ballot generation</div>
                <button className="step-button">Initialize</button>
              </div>

              {/* Ballot Pick-up */}
              <div className={`step-card ${completedSteps.Generate_Ballots ? 'completed' : ''}`} onClick={() => handleStep("generateBallots")}>
                {completedSteps.generateBallots && <div className="status-indicator">Verified</div>}
                <div className="images">
                  <img src={PO} alt="PO" className="image-lg" />
                  <img src={BallotPickup} alt="Ballot Pick-up" className="image-lg" />
                  <img src={Voter} alt="Voter" className="image-lg" />
                </div>
                <div className="step-title">Ballot pick-up</div>
                <button className="step-button">Process</button>
              </div>

              {/* Vote Casting */}
              <div className={`step-card ${completedSteps.Upload_Votes ? 'completed' : ''}`} onClick={() => handleStep("uploadVotes")}>
                {completedSteps.Upload_Votes && <div className="status-indicator">Verified</div>}
                <div className="images">
                  <img src={VoteCasting} alt="Vote Casting" className="image-lg"/>
                </div>
                <div className="step-title">Vote casting</div>
                <button className="step-button">Cast Votes</button>
              </div>

              {/* Receipt Scanning */}
              <div className="step-card" onClick={() => handleStep("uploadVotes")}>
                <div className="images">
                  <img src={PO} alt="PO" className="image-lg" />
                  <img src={ReceiptScanning} alt="Receipt Scanning" className="image-lg"/>
                  <img src={Voter} alt="Voter" className="image-lg" />
                </div>
                <div className="step-title">Receipt scanning</div>
                <button className="step-button">Scan</button>
              </div>

              {/* Vote Processing */}
              <div className={`step-card ${completedSteps.Mix_Decrypt ? 'completed' : ''}`} onClick={() => handleStep("mixAndDecrypt")}>
                {completedSteps.Mix_Decrypt && <div className="status-indicator">Verified</div>}
                <div className="images">
                  <img src={VoteProcessing} alt="Vote Processing" className="image-lg"/>
                </div>
                <div className="step-title">Vote Processing</div>
                <button className="step-button">Process</button>
              </div>
            </div>
          </div>

          {/* Trusted Section */}
          <div className="section trusted-section">
            <div className="section-header">
              Trusted Verification Components
            </div>
            <div className="steps-grid">
              {/* Ballot Audit */}
              <div className={`step-card ${completedSteps.Audit ? 'completed' : ''}`} onClick={() => handleStep("audit")}>
                {completedSteps.Audit && <div className="status-indicator">Verified</div>}
                <div className="images">
                  <img src={BallotPickup} alt="Ballot Pick-up" className="image-lg" />
                  <img src={BallotAudit} alt="Ballot Audit" className="image-lg"/>
                </div>
                <div className="step-title">Ballot audit</div>
                <button className="step-button">Verify</button>
              </div>

              {/* Receipt Verification */}
              <div className="step-card" onClick={() => handleStep("audit")}>
                <div className="images">
                  <img src={ReceiptScanning} alt="Receipt Scanning" className="image-lg"/>
                  <img src={ReceiptVerf} alt="Receipt Verification" className="image-lg"/>
                </div>
                <div className="step-title">Receipt verification</div>
                <button className="step-button">Verify</button>
              </div>

              {/* Decryption Verification */}
              <div className="step-card" onClick={() => handleStep("mixAndDecrypt")}>
                <div className="images">
                  <img src={VoteProcessing} alt="Vote Processing" className="image-lg"/>
                  <img src={DercyptVerf} alt="Decryption Verification" className="image-lg"/>
                </div>
                <div className="step-title">Decryption verification</div>
                <button className="step-button">Verify</button>
              </div>

              {/* VVPAT Verification */}
              <div className="step-card" onClick={() => handleStep("audit")}>
                <div className="images">
                  <img src={VVPAT} alt="VVPAT" className="image-lg"/>
                  <img src={VVPATVerf} alt="VVPAT Verification" className="image-lg"/>
                </div>
                <div className="step-title">VVPAT verification</div>
                <button className="step-button">Verify</button>
              </div>
            </div>
          </div>
        </div>

        {currentStep && (
          <div className="response-area">
            <div className="response-header">Current Step: {currentStep}</div>
            <div className="response-content">{response}</div>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
