import React, { useState } from "react";
import axios from "axios";
import "./Dashboard.css"; // Importing CSS
import BallotGeneration from "./icons/ballot-generator.png"
import BallotPickup from "./icons/ballots.png"
import VoteCasting from "./icons/ballotbox.png"
import ReceiptScanning from "./icons/receipt.png"
import VoteProcessing from "./icons/bb.png"
import BallotAudit from "./icons/auditors-phone.png"
import ReceiptVerf from "./icons/voter-phone.png"
import DercyptVerf from "./icons/univauditors-phone.png"
import VVPATVerf from "./icons/vvpatauditors.png"
import PO from "./icons/po.png"
import Voter from "./icons/voter.png"
const App = () => {
  const [currentStep, setCurrentStep] = useState("");
  const [response, setResponse] = useState("");

  // Function to handle API calls for each step
  const handleStep = async (step) => {
    setCurrentStep(step);
    try {
      let res;
      switch (step) {
        case "setup":
          res = await axios.post("/setup", { alpha: 1, electionId: 123 });
          break;
        case "generateBallots":
          res = await axios.post("/generate", { n: 10, electionId: 123 });
          break;
        case "uploadVotes":
          res = await axios.post("/upload", { votes: [] }); // Replace with actual payload
          break;
        case "mixAndDecrypt":
          res = await axios.post("/mix");
          break;
        case "audit":
          res = await axios.post("/audit", {
            commitment: [],
            booth_num: 1,
            bid: 101,
            election_id: 123,
          });
          break;
        default:
          res = { data: "Invalid step" };
      }
      setResponse(res.data);
    } catch (error) {
      console.error(error);
      setResponse("Error occurred while processing the request.");
    }
  };

  return (
    <div className="dashboard">
      <h1>Election Dashboard</h1>

      {/* Opaque and Untrusted Election Components */}
      <div className="opaque-section">
        <h2>Opaque and Untrusted Election Components</h2>
        <div className="steps-container">
          <div className="step">
            <img src={BallotGeneration} alt="Ballot Generation" className="image-small"/>
            <p>Ballot generation</p>
            <button onClick={() => handleStep("setup")}>Start</button>
          </div>
          <div className="step">
            <div className="top-row">
              <img src={BallotPickup} alt="Ballot Pick-up" className="image-large" />
              <img src={PO} alt="PO" className="image-small" />
            </div>
            <img src={Voter} alt="Voter" className="image-medium" />
            <p>Ballot pick-up</p>
            <button onClick={() => handleStep("generateBallots")}>Start</button>
        </div>
          <div className="step">
            <img src={VoteCasting} alt="Vote Casting" className="image-small"/>
            <p>Vote casting</p>
            <button onClick={() => handleStep("uploadVotes")}>Start</button>
          </div>
          <div className="step">
            <img src={ReceiptScanning} alt="Receipt Scanning" className="image-small"/>
            <p>Receipt scanning</p>
            <button onClick={() => handleStep("uploadVotes")}>Start</button>
          </div>
          <div className="step">
            <img src={VoteProcessing} alt="Vote Processing" className="image-small"/>
            <p>Vote Processing</p>
            <button onClick={() => handleStep("mixAndDecrypt")}>Start</button>
          </div>
        </div>
      </div>

      {/* Open Source and Trusted Verification Components */}
      <div className="trusted-section">
        <h2>Open Source and Trusted Verification Components</h2>
        <div className="steps-container">
          <div className="step">
            <img src={BallotAudit} alt="Ballot Audit" className="image-small"/>
            <p>Ballot audit</p>
            <button onClick={() => handleStep("audit")}>Start</button>
          </div>
          <div className="step">
            <img src={ReceiptVerf} alt="Receipt Verification" className="image-small"/>
            <p>Receipt verification</p>
            <button onClick={() => handleStep("audit")}>Start</button>
          </div>
          <div className="step">
            <img src={DercyptVerf} alt="Decryption Verification" className="image-small"/>
            <p>Decryption verification</p>
            <button onClick={() => handleStep("mixAndDecrypt")}>Start</button>
          </div>
          <div className="step">
            <img src={VVPATVerf} alt="VVPAT Verification" className="image-small"/>
            <p>VVPAT verification</p>
            <button onClick={() => handleStep("audit")}>Start</button>
          </div>
        </div>
      </div>

      {/* Response Section */}
      <div className="response-section">
        {currentStep && (
          <>
            <h3>Current Step: {currentStep}</h3>
            <pre>{response}</pre>
          </>
        )}
      </div>
    </div>
  );
};

export default App;
