                  <img src={BallotAudit} alt="Audit" className="image-lg"/>
                </div>
                <div className="step-title">Ballot Audit</div>
                <button className="step-button">Check Status</button>
              </div>

              {/* Voter Verification */}
              <div className={`step-card ${completedSteps.Voter_Verification ? 'completed' : ''}`} 
                   onClick={() => handleStepClick("Voter_Verification")}>
                {completedSteps.Voter_Verification && <div className="status-indicator">Verified</div>}
                <div className="images">
                  <img src={ReceiptVerf} alt="Voter Verification" className="image-lg"/>
                </div>
                <div className="step-title">Voter Verification</div>
                <button className="step-button">Check Status</button>
              </div>

              {/* Decryption Verification */}
              <div className={`step-card ${completedSteps.Decryption_Verification ? 'completed' : ''}`} 
                   onClick={() => handleStepClick("Decryption_Verification")}>
                {completedSteps.Decryption_Verification && <div className="status-indicator">Verified</div>}
                <div className="images">
                  <img src={DercyptVerf} alt="Decryption Verification" className="image-lg"/>
                </div>
                <div className="step-title">Decryption Verification</div>
                <button className="step-button">Check Status</button>
              </div>

              {/* VVPAT Verification */}
              <div className={`step-card ${completedSteps.VVPAT_Verification ? 'completed' : ''}`} 
                   onClick={() => handleStepClick("VVPAT_Verification")}>
                {completedSteps.VVPAT_Verification && <div className="status-indicator">Verified</div>}
                <div className="images">
                  <img src={VVPATVerf} alt="VVPAT Verification" className="image-lg"/>
                </div>
                <div className="step-title">VVPAT Verification</div>
                <button className="step-button">Check Status</button>
              </div>
            </div>
          </div>
        </div>

        <div className="status-overview">
          <div className="server-status">
            <h3>Processing Server Status (5000)</h3>
            {Object.entries(status5000).map(([key, value]) => (
              <div key={key} className={`status-item ${value}`}>
                {key}: {value}
              </div>
            ))}
          </div>
          <div className="server-status">
            <h3>Verification Server Status (7000)</h3>
            {Object.entries(status7000).map(([key, value]) => (
              <div key={key} className={`status-item ${value}`}>
                {key}: {value}
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
