import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Dashboard.css";

// Import all your icons
import BallotGeneration from "./icons/ballot-generator.png";
import VoteUpload from "./icons/vote-upload.png";
import MixDecrypt from "./icons/mix-decrypt.png";
import Audit from "./icons/audit.png";
import VoterVerification from "./icons/voter-verification.png";
import DecryptionVerification from "./icons/decryption-verification.png";
import VVPATVerification from "./icons/vvpat-verification.png";
import Setup from "./icons/setup.png";

const Dashboard = () => {
  const [completedSteps, setCompletedSteps] = useState({
    Setup: false,
    Generate_Ballots: false,
    Upload_Votes: false,
    Mix_Decrypt: false,
    Audit: false,
    Voter_Verification: false,
    Decryption_Verification: false,
    VVPAT_Verification: false
  });

  const [status5000, setStatus5000] = useState({
    generate: "pending",
    upload: "pending",
    decryption: "pending"
  });

  const [status7000, setStatus7000] = useState({
    audit: "pending",
    voterverf: "pending",
    decryption_sm: "pending",
    vvpatverf: "pending"
  });

  const [showSuccessDialog, setShowSuccessDialog] = useState(false);
  const [currentStep, setCurrentStep] = useState("");
  const [response, setResponse] = useState("");

  useEffect(() => {
    const fetchStatus = async () => {
      try {
        // Fetch status from both servers
        const [res5000, res7000] = await Promise.all([
          axios.get("http://localhost:5000/status"),
          axios.get("http://localhost:7000/status")
        ]);

        setStatus5000(res5000.data);
        setStatus7000(res7000.data);

        // Map backend status to frontend steps
        setCompletedSteps(prev => ({
          ...prev,
          Generate_Ballots: res5000.data.generate === "success",
          Upload_Votes: res5000.data.upload === "success",
          Mix_Decrypt: res5000.data.decryption === "success",
          Audit: res7000.data.audit === "success",
          Voter_Verification: res7000.data.voterverf === "success",
          Decryption_Verification: res7000.data.decryption_sm === "success",
          VVPAT_Verification: res7000.data.vvpatverf === "success"
        }));

      } catch (error) {
        console.error("Error fetching status:", error);
      }
    };

    fetchStatus();
    const intervalId = setInterval(fetchStatus, 5000);
    return () => clearInterval(intervalId);
  }, []);

  const handleStepClick = async (step) => {
    setCurrentStep(step);
    try {
      // Determine which server to call based on step
      const backendToFrontendMap = {
        Generate_Ballots: { port: 5000, endpoint: "generate" },
        Upload_Votes: { port: 5000, endpoint: "upload" },
        Mix_Decrypt: { port: 5000, endpoint: "decryption" },
        Audit: { port: 7000, endpoint: "audit" },
        Voter_Verification: { port: 7000, endpoint: "voterverf" },
        Decryption_Verification: { port: 7000, endpoint: "decryption_sm" },
        VVPAT_Verification: { port: 7000, endpoint: "vvpatverf" }
      };

      const { port, endpoint } = backendToFrontendMap[step];
      const response = await axios.post(
        `http://localhost:${port}/${endpoint}`
      );

      setResponse(`Success: ${response.data.message}`);
      setShowSuccessDialog(true);
      setTimeout(() => setShowSuccessDialog(false), 3000);

    } catch (error) {
      setResponse(`Error: ${error.response?.data?.message || error.message}`);
    }
  };

  const renderStatusSection = (status, port) => (
    <div className="status-section">
      <h3>Server {port} Status:</h3>
      <pre>{JSON.stringify(status, null, 2)}</pre>
    </div>
  );

  return (
    <div className="dashboard">
      <div className="header">
        <h1>Secure Election System Monitor</h1>
      </div>

      {showSuccessDialog && (
        <div className="success-dialog">
          {response}
        </div>
      )}

      <div className="content-area">
        <div className="main-sections">
          {/* Untrusted Section */}
          <div className="section untrusted-section">
            <div className="section-header">Untrusted Components</div>
            <div className="steps-grid">
              <StepCard
                step="Generate_Ballots"
                title="Ballot Generation"
                completed={completedSteps.Generate_Ballots}
                icon={BallotGeneration}
                onClick={handleStepClick}
              />
              <StepCard
                step="Upload_Votes"
                title="Vote Upload"
                completed={completedSteps.Upload_Votes}
                icon={VoteUpload}
                onClick={handleStepClick}
              />
              <StepCard
                step="Mix_Decrypt"
                title="Mix & Decrypt"
                completed={completedSteps.Mix_Decrypt}
                icon={MixDecrypt}
                onClick={handleStepClick}
              />
            </div>
          </div>

          {/* Trusted Section */}
          <div className="section trusted-section">
            <div className="section-header">Trusted Components</div>
            <div className="steps-grid">
              <StepCard
                step="Audit"
                title="Audit Process"
                completed={completedSteps.Audit}
                icon={Audit}
                onClick={handleStepClick}
              />
              <StepCard
                step="Voter_Verification"
                title="Voter Verification"
                completed={completedSteps.Voter_Verification}
                icon={VoterVerification}
                onClick={handleStepClick}
              />
              <StepCard
                step="Decryption_Verification"
                title="Decryption Verification"
                completed={completedSteps.Decryption_Verification}
                icon={DecryptionVerification}
                onClick={handleStepClick}
              />
              <StepCard
                step="VVPAT_Verification"
                title="VVPAT Verification"
                completed={completedSteps.VVPAT_Verification}
                icon={VVPATVerification}
                onClick={handleStepClick}
              />
            </div>
          </div>
        </div>

        <div className="status-display">
          {renderStatusSection(status5000, 5000)}
          {renderStatusSection(status7000, 7000)}
          {currentStep && (
            <div className="response-area">
              <div className="response-header">Current Operation: {currentStep}</div>
              <div className="response-content">{response}</div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

const StepCard = ({ step, title, completed, icon, onClick }) => (
  <div className={`step-card ${completed ? 'completed' : ''}`} onClick={() => onClick(step)}>
    {completed && <div className="status-indicator">Verified</div>}
    <div className="images">
      <img src={icon} alt={title} className="image-lg" />
    </div>
    <div className="step-title">{title}</div>
    <button className="step-button">Check Status</button>
  </div>
);

export default Dashboard;
