import React, { useState, useEffect } from "react";
import axios from "axios";
import io from "socket.io-client";
import "./Dashboard.css";

// Image Imports
import BallotGeneration from "./icons/ballot-generator.png";
import BallotPickup from "./icons/ballots.png";
import VoteCasting from "./icons/ballotbox.png";
import ReceiptScanning from "./icons/receipt.png";
import VoteProcessing from "./icons/bb.png";
import BallotAudit from "./icons/auditors-phone.png";
import ReceiptVerf from "./icons/voter-phone.png";
import DercyptVerf from "./icons/univauditors-phone.png";
import VVPATVerf from "./icons/vvpatauditors.png";

const App = () => {
  const [currentStep, setCurrentStep] = useState("");
  const [completedSteps, setCompletedSteps] = useState({});
  const [showSuccessDialog, setShowSuccessDialog] = useState(false);
  const [socket, setSocket] = useState(null);

  useEffect(() => {
    const newSocket = io("http://localhost:5000", { transports: ["websocket"] });
    setSocket(newSocket);
    return () => newSocket.disconnect();
  }, []);

  useEffect(() => {
    if (!socket) return;

    socket.on("step-completed", (step) => {
      setCompletedSteps((prev) => ({ ...prev, [step]: true }));
      setShowSuccessDialog(true);
      setTimeout(() => setShowSuccessDialog(false), 3000);
    });

    return () => socket.off("step-completed");
  }, [socket]);

  const handleStep = async (step) => {
    setCurrentStep(step);
    try {
      await axios.post(`http://localhost:5000/${step}`);
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div className="dashboard">
      <h1>Election Dashboard</h1>

      {showSuccessDialog && <div className="success-dialog">✓ Step completed successfully!</div>}

      {/* Opaque Section */}
      <div className="section">
        <h2>Opaque & Untrusted Election Components</h2>
        <div className="steps-container">
          <StepCard
            title="Ballot Generation"
            img={BallotGeneration}
            step="setup"
            handleStep={handleStep}
            completed={completedSteps.setup}
          />
          <StepCard
            title="Ballot Pick-up"
            img={BallotPickup}
            step="generateBallots"
            handleStep={handleStep}
            completed={completedSteps.generateBallots}
          />
        </div>
      </div>

      {/* Trusted Section */}
      <div className="section">
        <h2>Trusted Verification Components</h2>
        <div className="steps-container">
          <StepCard
            title="Ballot Audit"
            img={BallotAudit}
            step="audit"
            handleStep={handleStep}
            completed={completedSteps.audit}
          />
        </div>
      </div>
    </div>
  );
};

const StepCard = ({ title, img, step, handleStep, completed }) => (
  <div className={`step ${completed ? "completed" : ""}`} onClick={() => handleStep(step)}>
    <img src={img} alt={title} style={{ width: "60px", marginBottom: "10px" }} />
    <p>{title}</p>
    <button>{completed ? "Completed" : "Start"}</button>
    {completed && <div className="status-badge">✓ Verified</div>}
  </div>
);

export default App;
