import React, { useState, useEffect } from "react";
import axios from "axios";
import io from 'socket.io-client';
import "./Dashboard.css";

// Image imports
import BallotGeneration from "./icons/ballot-generator.png";
import BallotPickup from "./icons/ballots.png";
import VoteCasting from "./icons/ballotbox.png";
import ReceiptScanning from "./icons/receipt.png";
import VoteProcessing from "./icons/bb.png";
import BallotAudit from "./icons/auditors-phone.png";
import ReceiptVerf from "./icons/voter-phone.png";
import DercyptVerf from "./icons/univauditors-phone.png";
import VVPATVerf from "./icons/vvpatauditors.png";
import PO from "./icons/po.png";
import Voter from "./icons/voter.png";
import VVPAT from "./icons/vvpats.png";

const App = () => {
  const [currentStep, setCurrentStep] = useState("");
  const [response, setResponse] = useState("");
  const [completedSteps, setCompletedSteps] = useState({
    setup: false,
    generateBallots: false,
    uploadVotes: false,
    mixAndDecrypt: false,
    audit: false
  });
  const [showSuccessDialog, setShowSuccessDialog] = useState(false);
  const [socket, setSocket] = useState(null);

  // WebSocket initialization
  useEffect(() => {
    const newSocket = io('http://localhost:5000', {
      reconnection: true,
      transports: ['websocket'],
      withCredentials: true
    });
    setSocket(newSocket);

    return () => newSocket.disconnect();
  }, []);

  // Socket event listeners
  useEffect(() => {
    if (!socket) return;

    const handleStepComplete = (step) => {
      setCompletedSteps(prev => ({ ...prev, [step]: true }));
      setShowSuccessDialog(true);
      setTimeout(() => setShowSuccessDialog(false), 3000);
    };

    socket.on('step-completed', handleStepComplete);
    return () => socket.off('step-completed', handleStepComplete);
  }, [socket]);

  const handleStep = async (step) => {
    setCurrentStep(step);
    try {
      let res;
      switch (step) {
        case "setup":
          res = await axios.post("http://localhost:5000/setup", { 
            alpha: 1, 
            electionId: 123 
          });
          break;
        case "generateBallots":
          res = await axios.post("http://localhost:5000/generate", { 
            n: 10, 
            electionId: 123 
          });
          break;
        case "uploadVotes":
          res = await axios.post("http://localhost:5000/upload", { 
            votes: [] 
          });
          break;
        case "mixAndDecrypt":
          res = await axios.post("http://localhost:5000/mix");
          break;
        case "audit":
          res = await axios.post("http://localhost:5000/audit", {
            commitment: [],
            booth_num: 1,
            bid: 101,
            election_id: 123,
          });
          break;
        default:
          res = { data: "Invalid step" };
      }
      setResponse(JSON.stringify(res.data, null, 2));
    } catch (error) {
      console.error(error);
      setResponse(`Error: ${error.response?.data || error.message}`);
    }
  };

  return (
    <div className="dashboard">
      <h1>Election Dashboard</h1>

      {showSuccessDialog && (
        <div className="success-dialog">
          ✓ Step completed successfully!
        </div>
      )}

      <div className="opaque-section">
        <h2>Opaque and Untrusted Election Components</h2>
        <div className="steps-container">
          {/* Ballot Generation */}
          <div className={`step ${completedSteps.setup ? 'completed' : ''}`}>
            <div className="image-container">
              <img src={BallotGeneration} alt="Ballot Generation" className="image-medium"/>
              <img src={BallotPickup} alt="Ballot Pick-up" className="image-medium" />
            </div>
            <p>Ballot generation</p>
            <button onClick={() => handleStep("setup")}>Start</button>
            {completedSteps.setup && <div className="status-badge">✓ Verified</div>}
          </div>

          {/* Ballot Pick-up */}
          <div className={`step ${completedSteps.generateBallots ? 'completed' : ''}`}>
            <div className="image-container">
              <img src={PO} alt="PO" className="image-small" />
              <img src={BallotPickup} alt="Ballot Pick-up" className="image-medium" />
              <img src={Voter} alt="Voter" className="image-small" />
            </div>
            <p>Ballot pick-up</p>
            <button onClick={() => handleStep("generateBallots")}>Start</button>
            {completedSteps.generateBallots && <div className="status-badge">✓ Verified</div>}
          </div>

          {/* Vote Casting */}
          <div className={`step ${completedSteps.uploadVotes ? 'completed' : ''}`}>
            <div className="image-container">
              <img src={VoteCasting} alt="Vote Casting" className="image-medium"/>
            </div>
            <p>Vote casting</p>
            <button onClick={() => handleStep("uploadVotes")}>Start</button>
            {completedSteps.uploadVotes && <div className="status-badge">✓ Verified</div>}
          </div>

          {/* Receipt Scanning */}
          <div className={`step`}>
            <div className="image-container">
              <img src={PO} alt="PO" className="image-small" />
              <img src={ReceiptScanning} alt="Receipt Scanning" className="image-small"/>
              <img src={Voter} alt="Voter" className="image-small" />
            </div>
            <p>Receipt scanning</p>
            <button onClick={() => handleStep("uploadVotes")}>Start</button>
          </div>

          {/* Vote Processing */}
          <div className={`step ${completedSteps.mixAndDecrypt ? 'completed' : ''}`}>
            <div className="image-container">
              <img src={VoteProcessing} alt="Vote Processing" className="image-large"/>
            </div>
            <p>Vote Processing</p>
            <button onClick={() => handleStep("mixAndDecrypt")}>Start</button>
            {completedSteps.mixAndDecrypt && <div className="status-badge">✓ Verified</div>}
          </div>
        </div>
      </div>

      <div className="trusted-section">
        <h2>Open Source and Trusted Verification Components</h2>
        <div className="steps-container">
          {/* Ballot Audit */}
          <div className={`step ${completedSteps.audit ? 'completed' : ''}`}>
            <div className="image-container">
              <img src={BallotPickup} alt="Ballot Pick-up" className="image-small" />
              <img src={BallotAudit} alt="Ballot Audit" className="image-small"/>
            </div>
            <p>Ballot audit</p>
            <button onClick={() => handleStep("audit")}>Start</button>
            {completedSteps.audit && <div className="status-badge">✓ Verified</div>}
          </div>

          {/* Receipt Verification */}
          <div className={`step`}>
            <div className="image-container">
              <img src={ReceiptScanning} alt="Receipt Scanning" className="image-small"/>
              <img src={ReceiptVerf} alt="Receipt Verification" className="image-small"/>
            </div>
            <p>Receipt verification</p>
            <button onClick={() => handleStep("audit")}>Start</button>
          </div>

          {/* Decryption Verification */}
          <div className={`step`}>
            <div className="image-container">
              <img src={VoteProcessing} alt="Vote Processing" className="image-small"/>
              <img src={DercyptVerf} alt="Decryption Verification" className="image-small"/>
            </div>
            <p>Decryption verification</p>
            <button onClick={() => handleStep("mixAndDecrypt")}>Start</button>
          </div>

          {/* VVPAT Verification */}
          <div className={`step`}>
            <div className="image-container">
              <img src={VVPAT} alt="VVPAT" className="image-small"/>
              <img src={VVPATVerf} alt="VVPAT Verification" className="image-small"/>
            </div>
            <p>VVPAT verification</p>
            <button onClick={() => handleStep("audit")}>Start</button>
          </div>
        </div>
      </div>

      <div className="response-section">
        {currentStep && (
          <>
            <h3>Current Step: {currentStep}</h3>
            <pre>{response}</pre>
          </>
        )}
      </div>
    </div>
  );
};

export default App;
