import React, { useState } from "react";
import axios from "axios";

const App = () => {
  const [currentStep, setCurrentStep] = useState(null);
  const [response, setResponse] = useState("");

  // Function to handle API calls for each step
  const handleStep = async (step) => {
    setCurrentStep(step);
    try {
      let res;
      switch (step) {
        case "setup":
          res = await axios.post("/setup", { alpha: 1, electionId: 123 });
          break;
        case "generateBallots":
          res = await axios.post("/generate", { n: 10, electionId: 123 });
          break;
        case "uploadVotes":
          res = await axios.post("/upload", { votes: [] }); // Replace with actual payload
          break;
        case "mixAndDecrypt":
          res = await axios.post("/mix");
          break;
        case "audit":
          res = await axios.post("/audit", {
            commitment: [],
            booth_num: 1,
            bid: 101,
            election_id: 123,
          });
          break;
        default:
          res = { data: "Invalid step" };
      }
      setResponse(res.data);
    } catch (error) {
      console.error(error);
      setResponse("Error occurred while processing the request.");
    }
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>Election Dashboard</h1>
      <div style={{ display: "flex", justifyContent: "space-around" }}>
        {/* Opaque and Untrusted Election Components */}
        <div style={{ border: "2px solid red", padding: "10px" }}>
          <h2>Opaque and Untrusted Election Components</h2>
          <button onClick={() => handleStep("setup")}>Setup Keys</button>
          <button onClick={() => handleStep("generateBallots")}>
            Generate Ballots
          </button>
          <button onClick={() => handleStep("uploadVotes")}>Upload Votes</button>
        </div>

        {/* Open Source and Trusted Verification Components */}
        <div style={{ border: "2px solid green", padding: "10px" }}>
          <h2>Open Source and Trusted Verification Components</h2>
          <button onClick={() => handleStep("mixAndDecrypt")}>
            Mix & Decrypt Votes
          </button>
          <button onClick={() => handleStep("audit")}>Audit Ballot</button>
        </div>
      </div>

      {/* Display current step and response */}
      <div style={{ marginTop: "20px" }}>
        <h3>Current Step: {currentStep}</h3>
        <pre>{response}</pre>
      </div>
    </div>
  );
};

export default App;
