// AttackSimulationDashboard.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Line, Bar } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';
Chart.register(...registerables);

const App = () => {
  const [simulationData, setSimulationData] = useState({
    votes: [],
    attacks: [],
    auditResults: []
  });
  const [selectedAttack, setSelectedAttack] = useState('');

  // Real-time updates using Server-Sent Events
  useEffect(() => {
    const eventSource = new EventSource('http://localhost:5000/api/simulation-events');

    eventSource.onmessage = (e) => {
      const data = JSON.parse(e.data);
      setSimulationData(prev => ({
        votes: [...prev.votes, data.vote],
        attacks: data.attack ? [...prev.attacks, data.attack] : prev.attacks,
        auditResults: data.audit ? [...prev.auditResults, data.audit] : prev.auditResults
      }));
    };

    return () => eventSource.close();
  }, []);

  // Attack simulation handlers
  const simulateAttack = async (attackType) => {
    try {
      const response = await axios.post('http://localhost:5000/api/simulate-attack', {
        attackType,
        electionId: 'current-election'
      });

      // Update visualization with attack result
      setSimulationData(prev => ({
        ...prev,
        attacks: [...prev.attacks, response.data]
      }));
    } catch (error) {
      console.error('Attack simulation failed:', error);
    }
  };

  // Chart data configurations
  const voteDistributionChart = {
    labels: ['Alice', 'Bob', 'Carol', 'David'],
    datasets: [{
      label: 'Vote Distribution',
      data: simulationData.votes.reduce((acc, vote) => {
        acc[vote.candidate] = (acc[vote.candidate] || 0) + 1;
        return acc;
      }, {}),
      backgroundColor: 'rgba(75,192,192,0.6)'
    }]
  };

  const attackTimelineChart = {
    labels: simulationData.attacks.map((_, i) => `Attack ${i + 1}`),
    datasets: [{
      label: 'Attack Severity',
      data: simulationData.attacks.map(a => a.severity),
      borderColor: 'rgba(255,99,132,1)',
      fill: false
    }]
  };

  return (
    <div className="dashboard-container">
      <div className="attack-controls">
        <h2>Simulation Controls</h2>
        <select onChange={(e) => setSelectedAttack(e.target.value)}>
          <option value="">Select Attack Type</option>
          <option value="BALLOT_STUFFING">Ballot Stuffing</option>
          <option value="REPLAY_ATTACK">Replay Attack</option>
          <option value="DECRYPTION_ATTACK">Decryption Attack</option>
        </select>
        <button onClick={() => simulateAttack(selectedAttack)}>
          Execute Attack
        </button>
      </div>

      <div className="data-visualization">
        <div className="chart-container">
          <h3>Vote Distribution</h3>
          <Bar data={voteDistributionChart} />
        </div>

        <div className="chart-container">
          <h3>Attack Timeline</h3>
          <Line data={attackTimelineChart} />
        </div>
      </div>

      <div className="audit-results">
        <h3>Real-time Audit Logs</h3>
        <ul>
          {simulationData.auditResults.map((result, index) => (
            <li key={index} className={result.success ? 'success' : 'failure'}>
              {result.timestamp}: {result.message}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default App;
