import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Dashboard.css";

// Import all icons as specified
import BallotGeneration from "./icons/ballot-generator.png";
import BallotPickup from "./icons/ballots.png";
import VoteCasting from "./icons/ballotbox.png";
import ReceiptScanning from "./icons/receipt.png";
import VoteProcessing from "./icons/bb.png";
import BallotAudit from "./icons/auditors-phone.png";
import ReceiptVerf from "./icons/voter-phone.png";
import DercyptVerf from "./icons/univauditors-phone.png";
import VVPATVerf from "./icons/vvpatauditors.png";
import PO from "./icons/po.png";
import Voter from "./icons/voter.png";
import VVPAT from "./icons/vvpats.png";

const App = () => {
  const [completedSteps, setCompletedSteps] = useState({
    Setup: false,
    Generate_Ballots: false,
    Upload_Votes: false,
    Mix_Decrypt: false,
    Audit: false,
    Voter_Verification: false,
    Decryption_Verification: false,
    VVPAT_Verification: false
  });

  const [status5000, setStatus5000] = useState({
    generate: "pending",
    upload: "pending",
    decryption: "pending"
  });

  const [status7000, setStatus7000] = useState({
    audit: "pending",
    voterverf: "pending",
    decryption_sm: "pending",
    vvpatverf: "pending"
  });

  const [showSuccessDialog, setShowSuccessDialog] = useState(false);
  const [currentStep, setCurrentStep] = useState("");
  const [response, setResponse] = useState("");

  useEffect(() => {
    const fetchStatus = async () => {
      try {
        // Fetch status from both servers
        const [res5000, res7000] = await Promise.all([
          axios.get("http://localhost:5000/status"),
          axios.get("http://localhost:7000/status")
        ]);

        // Update status states
        setStatus5000(res5000.data);
        setStatus7000(res7000.data);

        // Map server statuses to completedSteps
        setCompletedSteps(prev => ({
          ...prev,
          Generate_Ballots: res5000.data.generate === "success",
          Upload_Votes: res5000.data.upload === "success",
          Mix_Decrypt: res5000.data.decryption === "success",
          Audit: res7000.data.audit === "success",
          Voter_Verification: res7000.data.voterverf === "success",
          Decryption_Verification: res7000.data.decryption_sm === "success",
          VVPAT_Verification: res7000.data.vvpatverf === "success"
        }));

      } catch (error) {
        console.error("Error fetching status:", error);
      }
    };

    fetchStatus();
    const intervalId = setInterval(fetchStatus, 5000);
    return () => clearInterval(intervalId);
  }, []);

  const handleStepClick = (step) => {
    setCurrentStep(step);
    // Get status from appropriate server
    const status = step === "Audit" || step === "Voter_Verification" || 
                   step === "Decryption_Verification" || step === "VVPAT_Verification" 
                   ? status7000 : status5000;

    const statusKey = {
      "Generate_Ballots": "generate",
      "Upload_Votes": "upload",
      "Mix_Decrypt": "decryption",
      "Audit": "audit",
      "Voter_Verification": "voterverf",
      "Decryption_Verification": "decryption_sm",
      "VVPAT_Verification": "vvpatverf"
    }[step];

    setResponse(`${step.replace(/_/g, ' ')} Status: ${status[statusKey]}`);
    setShowSuccessDialog(true);
    setTimeout(() => setShowSuccessDialog(false), 3000);
  };

  rreturn (
    <div className="dashboard">
      <div className="header">
        <h1>Election Process Monitor</h1>
      </div>

      <div className="content-area">
        <div className="main-sections">
          {/* Untrusted Section */}
          <div className="section untrusted-section">
            <div className="section-header">
              Core Election Components
            </div>
            <div className="steps-grid">
              {/* Setup Card */}
              <div className={`step-card ${completedSteps.Setup ? 'completed' : ''}`}>
                {completedSteps.Setup && <div className="status-indicator">Verified</div>}
                <div className="images">
                  <img src={PO} alt="Setup" className="image-lg" />
                </div>
                <div className="step-title">System Initialization</div>
              </div>

              {/* Ballot Generation Card */}
              <div className={`step-card ${completedSteps.Generate_Ballots ? 'completed' : ''}`} 
                   onClick={() => handleStepClick('Generate_Ballots')}>
                {completedSteps.Generate_Ballots && <div className="status-indicator">Verified</div>}
                <div className="images">
                  <img src={BallotGeneration} alt="Ballot Generation" className="image-lg" />
                </div>
                <div className="step-title">Ballot Generation</div>
                <button className="step-button">Verify Status</button>
              </div>

              {/* Add other cards following the same pattern */}

            </div>
          </div>

          {/* Trusted Section */}
          <div className="section trusted-section">
            <div className="section-header">
              Verification Components
            </div>
            <div className="steps-grid">
              {/* Audit Card */}
              <div className={`step-card ${completedSteps.Audit ? 'completed' : ''}`} 
                   onClick={() => handleStepClick('Audit')}>
                {completedSteps.Audit && <div className="status-indicator">Verified</div>}
                <div className="images">
                  <img src={BallotAudit} alt="Audit" className="image-lg" />
                </div>
                <div className="step-title">Ballot Audit</div>
                <button className="step-button">Verify Status</button>
              </div>

              {/* Add other verification cards */}

            </div>
          </div>
        </div>

        {/* Status Overview Panel */}
        <div className="status-overview">
          <div className="server-status">
            <h3>Processing Server (5000)</h3>
            {Object.entries(status5000).map(([key, value]) => (
              <div key={key} className={`status-item ${value}`}>
                <span>{key.replace(/_/g, ' ')}</span>
                <span>{value}</span>
              </div>
            ))}
          </div>

          <div className="server-status">
            <h3>Verification Server (7000)</h3>
            {Object.entries(status7000).map(([key, value]) => (
              <div key={key} className={`status-item ${value}`}>
                <span>{key.replace(/_/g, ' ')}</span>
                <span>{value}</span>
              </div>
            ))}
          </div>
        </div>
      </div>

      {showSuccessDialog && (
        <div className="success-dialog">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
            <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
          </svg>
          {response}
        </div>
      )}
    </div>
  );
};

export default App;
