import React, { useState } from "react";
import axios from "axios";
import "./Dashboard.css"; // Importing CSS

const App = () => {
  const [currentStep, setCurrentStep] = useState("");
  const [response, setResponse] = useState("");

  // Function to handle API calls for each step
  const handleStep = async (step) => {
    setCurrentStep(step);
    try {
      let res;
      switch (step) {
        case "setup":
          res = await axios.post("/setup", { alpha: 1, electionId: 123 });
          break;
        case "generateBallots":
          res = await axios.post("/generate", { n: 10, electionId: 123 });
          break;
        case "uploadVotes":
          res = await axios.post("/upload", { votes: [] }); // Replace with actual payload
          break;
        case "mixAndDecrypt":
          res = await axios.post("/mix");
          break;
        case "audit":
          res = await axios.post("/audit", {
            commitment: [],
            booth_num: 1,
            bid: 101,
            election_id: 123,
          });
          break;
        default:
          res = { data: "Invalid step" };
      }
      setResponse(res.data);
    } catch (error) {
      console.error(error);
      setResponse("Error occurred while processing the request.");
    }
  };

  return (
    <div className="dashboard">
      <h1>Election Dashboard</h1>
      <div className="sections">
        {/* Opaque and Untrusted Election Components */}
        <div className="opaque-section">
          <h2>Opaque and Untrusted Election Components</h2>
          <div className="step-box">
            <button onClick={() => handleStep("setup")}>Ballot Generation</button>
            <button onClick={() => handleStep("generateBallots")}>Ballot Pick-up</button>
            <button onClick={() => handleStep("uploadVotes")}>Vote Casting</button>
            <button onClick={() => handleStep("uploadVotes")}>Receipt Scanning</button>
            <button onClick={() => handleStep("mixAndDecrypt")}>Vote Processing</button>
          </div>
        </div>

        {/* Open Source and Trusted Verification Components */}
        <div className="trusted-section">
          <h2>Open Source and Trusted Verification Components</h2>
          <div className="step-box">
            <button onClick={() => handleStep("audit")}>Ballot Audit</button>
            <button onClick={() => handleStep("audit")}>Receipt Verification</button>
            <button onClick={() => handleStep("mixAndDecrypt")}>
              Decryption Verification
            </button>
            <button onClick={() => handleStep("audit")}>VVPAT Verification</button>
          </div>
        </div>
      </div>

      {/* Display current step and response */}
      <div className="response-section">
        <h3>Current Step: {currentStep}</h3>
        <pre>{response}</pre>
      </div>
    </div>
  );
};

export default App;
